// ==================================================================
// AUTH SLICE - REEMPLAZO DEL CONTEXT API
// ==================================================================

import { createSlice, createAsyncThunk, PayloadAction } from '@reduxjs/toolkit';
import { 
  loginUser, 
  refreshToken as apiRefreshToken, 
  logoutUser, 
  verifyAuth 
} from '../../services/api';

export interface AuthState {
  isAuthenticated: boolean;
  userRole: string | null;
  isInitialized: boolean;
  isLoading: boolean;
  error: string | null;
  token: string | null;
  refreshToken: string | null;
}

const initialState: AuthState = {
  isAuthenticated: false,
  userRole: null,
  isInitialized: false,
  isLoading: false,
  error: null,
  token: null,
  refreshToken: null,
};

// Async thunks
export const login = createAsyncThunk(
  'auth/login',
  async (credentials: { username: string; password: string }, { rejectWithValue }) => {
    try {
      const response = await loginUser(credentials.username, credentials.password);

      // La API maneja las cookies automáticamente, pero obtener perfil para rol
      const profile = await verifyAuth();

      return {
        token: response.access_token,
        userRole: profile.role,
      };
    } catch (error: any) {
      return rejectWithValue(error.response?.data?.detail || 'Error en el login');
    }
  }
);

export const refreshAuthToken = createAsyncThunk(
  'auth/refreshToken',
  async (_, { rejectWithValue }) => {
    try {
      const refreshToken = localStorage.getItem('refresh_token');
      if (!refreshToken) {
        throw new Error('No refresh token available');
      }

      const response = await authApi.refreshToken(refreshToken);

      // Actualizar tokens
      localStorage.setItem('access_token', response.data.access_token);
      localStorage.setItem('refresh_token', response.data.refresh_token);

      return {
        token: response.data.access_token,
        refreshToken: response.data.refresh_token,
      };
    } catch (error: any) {
      // Limpiar tokens inválidos
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');
      localStorage.removeItem('user_role');

      return rejectWithValue(error.response?.data?.detail || 'Error al refrescar token');
    }
  }
);

export const checkAuthStatus = createAsyncThunk(
  'auth/checkStatus',
  async (_, { dispatch, rejectWithValue }) => {
    try {
      const token = localStorage.getItem('access_token');
      const userRole = localStorage.getItem('user_role');

      if (!token) {
        throw new Error('No token found');
      }

      // Verificar si el token es válido
      const response = await authApi.verifyToken(token);

      return {
        token,
        userRole,
        isValid: response.data.valid,
      };
    } catch (error: any) {
      // Si el token no es válido, intentar refrescar
      const refreshResult = await dispatch(refreshAuthToken());
      if (refreshAuthToken.fulfilled.match(refreshResult)) {
        return {
          token: refreshResult.payload.token,
          userRole: localStorage.getItem('user_role'),
          isValid: true,
        };
      }

      return rejectWithValue('Authentication verification failed');
    }
  }
);

export const logout = createAsyncThunk(
  'auth/logout',
  async (_, { rejectWithValue }) => {
    try {
      const token = localStorage.getItem('access_token');
      if (token) {
        await authApi.logout(token);
      }
    } catch (error) {
      // Ignorar errores de logout en el servidor
      console.warn('Logout server error:', error);
    } finally {
      // Siempre limpiar tokens locales
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');
      localStorage.removeItem('user_role');
    }
  }
);

// Slice
const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    clearError: (state) => {
      state.error = null;
    },
    resetAuth: (state) => {
      Object.assign(state, initialState);
    },
  },
  extraReducers: (builder) => {
    builder
      // Login
      .addCase(login.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(login.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isAuthenticated = true;
        state.token = action.payload.token;
        state.refreshToken = action.payload.refreshToken;
        state.userRole = action.payload.userRole;
        state.error = null;
      })
      .addCase(login.rejected, (state, action) => {
        state.isLoading = false;
        state.isAuthenticated = false;
        state.token = null;
        state.refreshToken = null;
        state.userRole = null;
        state.error = action.payload as string;
      })

      // Refresh token
      .addCase(refreshAuthToken.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(refreshAuthToken.fulfilled, (state, action) => {
        state.isLoading = false;
        state.token = action.payload.token;
        state.refreshToken = action.payload.refreshToken;
        state.error = null;
      })
      .addCase(refreshAuthToken.rejected, (state, action) => {
        state.isLoading = false;
        state.isAuthenticated = false;
        state.token = null;
        state.refreshToken = null;
        state.userRole = null;
        state.error = action.payload as string;
      })

      // Check auth status
      .addCase(checkAuthStatus.pending, (state) => {
        state.isLoading = true;
        state.isInitialized = false;
      })
      .addCase(checkAuthStatus.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isInitialized = true;
        state.isAuthenticated = action.payload.isValid;
        state.token = action.payload.token;
        state.userRole = action.payload.userRole;
        state.error = null;
      })
      .addCase(checkAuthStatus.rejected, (state, action) => {
        state.isLoading = false;
        state.isInitialized = true;
        state.isAuthenticated = false;
        state.token = null;
        state.refreshToken = null;
        state.userRole = null;
        state.error = action.payload as string;
      })

      // Logout
      .addCase(logout.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(logout.fulfilled, (state) => {
        Object.assign(state, initialState, { isInitialized: true });
      })
      .addCase(logout.rejected, (state) => {
        // Siempre limpiar el estado incluso si el logout falla
        Object.assign(state, initialState, { isInitialized: true });
      });
  },
});

export const { clearError, resetAuth } = authSlice.actions;
export default authSlice.reducer;

// Selectores
export const selectAuth = (state: { auth: AuthState }) => state.auth;
export const selectIsAuthenticated = (state: { auth: AuthState }) => state.auth.isAuthenticated;
export const selectUserRole = (state: { auth: AuthState }) => state.auth.userRole;
export const selectIsInitialized = (state: { auth: AuthState }) => state.auth.isInitialized;
export const selectIsLoading = (state: { auth: AuthState }) => state.auth.isLoading;
export const selectAuthError = (state: { auth: AuthState }) => state.auth.error;
export const selectToken = (state: { auth: AuthState }) => state.auth.token;
