import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { loginUser } from '../services/api';

const LoginPage: React.FC = () => {
  const [username, setUsername] = useState('admin');
  const [password, setPassword] = useState('admin123');
  const [error, setError] = useState<string | null>(null);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    console.log('üîê Iniciando login con:', { username, password: '***' });

    try {
      console.log('üì§ Llamando a loginUser...');
      const response = await loginUser(username, password);
      console.log('‚úÖ Login response:', response);

      console.log('üîç Verificando estado de autenticaci√≥n...');
      // Verificar el estado de autenticaci√≥n despu√©s del login
      const isAuthenticated = await login();
      console.log('üîç Estado autenticaci√≥n:', isAuthenticated);

      if (isAuthenticated) {
        console.log('üéâ Login exitoso, navegando a dashboard');
        navigate('/dashboard');
      } else {
        console.log('‚ùå Login fall√≥ en verificaci√≥n');
        setError("Error en la autenticaci√≥n - las credenciales fueron aceptadas pero la verificaci√≥n fall√≥");
      }
    } catch (err: any) {
      console.error('‚ùå Login error:', err);
      setError(err.message || "Credenciales inv√°lidas");
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: '50px auto', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>Iniciar Sesi√≥n</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="username" style={{ display: 'block', marginBottom: '5px' }}>Usuario:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
            required
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label htmlFor="password" style={{ display: 'block', marginBottom: '5px' }}>Contrase√±a:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit" style={{ width: '100%', padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
          Ingresar
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
