import React, { useState, useEffect } from 'react';
import { Product, ProductListResponse } from '../types';
import Pagination from '../components/Pagination';
import ErrorNotification from '../components/ErrorNotification';
import ProductNameAutocomplete from '../components/ProductNameAutocomplete';
import { useProductsApi } from '../hooks/useApiError';
import { useSkuValidation } from '../hooks/useSkuValidation';

const InventoryPage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [productListData, setProductListData] = useState<ProductListResponse | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [newProduct, setNewProduct] = useState<Partial<Product>>({
    sku: '', name: ''
  });
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  // Usar el hook de API con manejo de errores
  const {
    error,
    isLoading,
    clearError,
    createProduct,
    updateProduct,
    getProducts
  } = useProductsApi();

  // Hook para validaci√≥n de SKU en tiempo real
  const skuValidation = useSkuValidation();
  const editSkuValidation = useSkuValidation(editingProduct?.id);

  useEffect(() => {
    fetchProducts();
  }, [currentPage]);

  const fetchProducts = async () => {
    const skip = (currentPage - 1) * itemsPerPage;
    const data = await getProducts(skip, itemsPerPage);

    if (data) {
      if (Array.isArray(data)) {
        setProducts(data);
        setProductListData({ products: data, total: data.length, skip: skip, limit: itemsPerPage, has_next: false });
      } else {
        setProductListData(data);
        setProducts(data.products);
      }
    }
  };

  const handleSkuChange = (sku: string) => {
    setNewProduct({ ...newProduct, sku });
    skuValidation.validateSku(sku);
  };

  const handleEditSkuChange = (sku: string) => {
    if (editingProduct) {
      setEditingProduct({ ...editingProduct, sku });
      editSkuValidation.validateSku(sku);
    }
  };

  const handleCreateProduct = async (e: React.FormEvent) => {
    e.preventDefault();
    setSuccess(null);

    // Verificar que el SKU est√© disponible antes de crear
    if (skuValidation.isAvailable === false) {
      return; // No permitir crear si el SKU no est√° disponible
    }

    await createProduct(newProduct as Product, () => {
      setSuccess("Producto creado con √©xito.");
      setNewProduct({ sku: '', name: '' });
      skuValidation.clearValidation();

      // Volver a la primera p√°gina despu√©s de crear
      if (currentPage !== 1) {
        setCurrentPage(1);
      } else {
        fetchProducts();
      }
    });
  };

  const handleUpdateProduct = async (e: React.FormEvent) => {
    e.preventDefault();
    setSuccess(null);

    if (!editingProduct) return;

    // Verificar que el SKU est√© disponible antes de actualizar
    if (editSkuValidation.isAvailable === false) {
      return; // No permitir actualizar si el SKU no est√° disponible
    }

    await updateProduct(editingProduct.id, editingProduct, () => {
      setSuccess("Producto actualizado con √©xito.");
      setEditingProduct(null);
      editSkuValidation.clearValidation();
      fetchProducts();
    });
  };

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
    setEditingProduct(null); // Cerrar edici√≥n al cambiar p√°gina
    editSkuValidation.clearValidation(); // Limpiar validaci√≥n al cambiar p√°gina
  };

  const totalPages = productListData ? Math.ceil(productListData.total / itemsPerPage) : 1;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Gesti√≥n de Inventario</h1>

      {/* Notificaci√≥n de error */}
      <ErrorNotification 
        error={error} 
        onClose={clearError} 
      />

      {/* Mensaje de √©xito */}
      {success && (
        <div style={{ 
          padding: '10px', 
          backgroundColor: '#d4edda', 
          border: '1px solid #c3e6cb', 
          borderRadius: '4px', 
          color: '#155724',
          marginBottom: '20px'
        }}>
          {success}
        </div>
      )}

      <h2>Crear Nuevo Producto</h2>
      <form onSubmit={handleCreateProduct} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', marginBottom: '20px' }}>
        <div style={{ position: 'relative' }}>
          <input 
            type="text" 
            placeholder="SKU (ej: CASE001)" 
            value={newProduct.sku} 
            onChange={e => handleSkuChange(e.target.value)}
            style={{
              width: '100%',
              padding: '8px',
              border: `2px solid ${
                skuValidation.isValidating ? '#ffc107' : 
                skuValidation.isAvailable === false ? '#dc3545' : 
                skuValidation.isAvailable === true ? '#28a745' : '#ced4da'
              }`,
              borderRadius: '4px',
              fontSize: '14px'
            }}
            required 
          />
          {skuValidation.isValidating && (
            <small style={{ color: '#ffc107', display: 'block', marginTop: '2px' }}>
              üîç Verificando SKU...
            </small>
          )}
          {skuValidation.isAvailable === false && (
            <small style={{ color: '#dc3545', display: 'block', marginTop: '2px' }}>
              ‚ùå {skuValidation.message}
            </small>
          )}
          {skuValidation.isAvailable === true && (
            <small style={{ color: '#28a745', display: 'block', marginTop: '2px' }}>
              ‚úÖ {skuValidation.message}
            </small>
          )}
          {skuValidation.error && (
            <small style={{ color: '#dc3545', display: 'block', marginTop: '2px' }}>
              ‚ö†Ô∏è {skuValidation.error}
            </small>
          )}
        </div>
        <input type="text" placeholder="Nombre del Producto (ej: Funda iPhone 14)" value={newProduct.name} onChange={e => setNewProduct({ ...newProduct, name: e.target.value })} required />
        <input type="number" placeholder="Precio Costo en COP (ej: 15000)" value={newProduct.cost_price || ''} onChange={e => setNewProduct({ ...newProduct, cost_price: e.target.value ? parseFloat(e.target.value) : 0 })} required />
        <input type="number" placeholder="Precio Venta en COP (ej: 25000)" value={newProduct.selling_price || ''} onChange={e => setNewProduct({ ...newProduct, selling_price: e.target.value ? parseFloat(e.target.value) : 0 })} required />
        <input type="number" placeholder="Cantidad en Stock (ej: 50)" value={newProduct.stock_quantity || ''} onChange={e => setNewProduct({ ...newProduct, stock_quantity: e.target.value ? parseInt(e.target.value) : 0 })} required />
        <button 
          type="submit" 
          disabled={isLoading || skuValidation.isAvailable === false || skuValidation.isValidating}
          style={{
            padding: '8px 16px',
            backgroundColor: skuValidation.isAvailable === false ? '#6c757d' : '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: skuValidation.isAvailable === false ? 'not-allowed' : 'pointer',
            opacity: skuValidation.isAvailable === false ? 0.6 : 1
          }}
        >
          {isLoading ? 'Creando...' : 
           skuValidation.isAvailable === false ? 'SKU no disponible' :
           'Crear Producto'}
        </button>
      </form>

      <h2>Lista de Productos</h2>
      {productListData && (
        <div style={{ marginBottom: '10px', fontSize: '14px', color: '#666' }}>
          Mostrando {products.length} de {productListData.total} productos
        </div>
      )}

      {isLoading && (
        <div style={{ textAlign: 'center', padding: '20px' }}>
          Cargando productos...
        </div>
      )}

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#f8f9fa' }}>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>SKU</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>Nombre</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'right' }}>Costo (COP)</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'right' }}>Venta (COP)</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'right' }}>Stock</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'right' }}>Valor Stock (COP)</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id} style={{ 
              backgroundColor: product.stock_quantity === 0 ? '#fff3cd' : 'white' 
            }}>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{product.sku}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {product.name}
                {product.stock_quantity === 0 && (
                  <span style={{ color: '#856404', fontSize: '12px', marginLeft: '5px' }}>
                    (Sin stock)
                  </span>
                )}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'right' }}>
                ${product.cost_price.toLocaleString('es-CO')}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'right' }}>
                ${product.selling_price.toLocaleString('es-CO')}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'right' }}>
                {product.stock_quantity}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'right' }}>
                ${(product.stock_quantity * product.cost_price).toLocaleString('es-CO')}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'center' }}>
                <button 
                  onClick={() => setEditingProduct(product)}
                  style={{
                    padding: '6px 12px',
                    backgroundColor: '#6c757d',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                  disabled={isLoading}
                >
                  Editar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Componente de paginaci√≥n */}
      <Pagination
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={handlePageChange}
        disabled={isLoading}
      />

      {editingProduct && (
        <div style={{ 
          marginTop: '20px', 
          border: '1px solid #ccc', 
          padding: '20px',
          borderRadius: '4px',
          backgroundColor: '#f8f9fa'
        }}>
          <h2>Editar Producto: {editingProduct.name}</h2>
          <form onSubmit={handleUpdateProduct} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
            <div style={{ position: 'relative' }}>
              <input 
                type="text" 
                placeholder="SKU (ej: CASE001)" 
                value={editingProduct.sku} 
                onChange={e => handleEditSkuChange(e.target.value)}
                style={{
                  width: '100%',
                  padding: '8px',
                  border: `2px solid ${
                    editSkuValidation.isValidating ? '#ffc107' : 
                    editSkuValidation.isAvailable === false ? '#dc3545' : 
                    editSkuValidation.isAvailable === true ? '#28a745' : '#ced4da'
                  }`,
                  borderRadius: '4px',
                  fontSize: '14px'
                }}
                required 
              />
              {editSkuValidation.isValidating && (
                <small style={{ color: '#ffc107', display: 'block', marginTop: '2px' }}>
                  üîç Verificando SKU...
                </small>
              )}
              {editSkuValidation.isAvailable === false && (
                <small style={{ color: '#dc3545', display: 'block', marginTop: '2px' }}>
                  ‚ùå {editSkuValidation.message}
                </small>
              )}
              {editSkuValidation.isAvailable === true && (
                <small style={{ color: '#28a745', display: 'block', marginTop: '2px' }}>
                  ‚úÖ {editSkuValidation.message}
                </small>
              )}
              {editSkuValidation.error && (
                <small style={{ color: '#dc3545', display: 'block', marginTop: '2px' }}>
                  ‚ö†Ô∏è {editSkuValidation.error}
                </small>
              )}
            </div>
            <input 
              type="text" 
              placeholder="Nombre del Producto (ej: Funda iPhone 14)" 
              value={editingProduct.name} 
              onChange={e => setEditingProduct({ ...editingProduct, name: e.target.value })} 
              required 
            />
            <input 
              type="number" 
              step="0.01"
              placeholder="Precio Costo en COP (ej: 15000)" 
              value={editingProduct.cost_price} 
              onChange={e => setEditingProduct({ ...editingProduct, cost_price: parseFloat(e.target.value) })} 
              required 
            />
            <input 
              type="number" 
              step="0.01"
              placeholder="Precio Venta en COP (ej: 25000)" 
              value={editingProduct.selling_price} 
              onChange={e => setEditingProduct({ ...editingProduct, selling_price: parseFloat(e.target.value) })} 
              required 
            />
            <input 
              type="number" 
              placeholder="Cantidad en Stock (ej: 50)" 
              value={editingProduct.stock_quantity} 
              onChange={e => setEditingProduct({ ...editingProduct, stock_quantity: parseInt(e.target.value) })} 
              required 
            />
            <div style={{ display: 'flex', gap: '10px' }}>
              <button 
                type="submit" 
                disabled={isLoading || editSkuValidation.isAvailable === false || editSkuValidation.isValidating}
                style={{
                  padding: '8px 16px',
                  backgroundColor: editSkuValidation.isAvailable === false ? '#6c757d' : '#28a745',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: editSkuValidation.isAvailable === false ? 'not-allowed' : 'pointer',
                  opacity: editSkuValidation.isAvailable === false ? 0.6 : 1
                }}
              >
                {isLoading ? 'Guardando...' : 
                 editSkuValidation.isAvailable === false ? 'SKU no disponible' :
                 'Guardar Cambios'}
              </button>
              <button 
                type="button" 
                onClick={() => {
                  setEditingProduct(null);
                  editSkuValidation.clearValidation();
                }}
                style={{
                  padding: '8px 16px',
                  backgroundColor: '#6c757d',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Cancelar
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default InventoryPage;
