import React, { useState } from 'react';
import { Product, CartItem, SalePayload } from '../types';
import { searchProducts, postSale } from '../services/api';
// import { useAuth } from '../context/AuthContext';

const POSPage: React.FC = () => {
    const [cart, setCart] = useState<CartItem[]>([]);
    const [query, setQuery] = useState('');
    const [searchResults, setSearchResults] = useState<Product[]>([]);
    const [error, setError] = useState<string | null>(null);
    const [success, setSuccess] = useState<string | null>(null);
    // const { isAuthenticated } = useAuth(); // Para verificar el rol si es necesario

    const handleSearch = async () => {
        if (query.trim() === '') {
            setSearchResults([]);
            return;
        }
        try {
            const foundProducts = await searchProducts(query);
            setSearchResults(foundProducts);
        } catch (err) {
            setError("Error al buscar productos.");
        }
    };

    const handleAddProductToCart = (product: Product) => {
        setCart(prevCart => {
            const existingItem = prevCart.find(item => item.id === product.id);
            if (existingItem) {
                if (existingItem.quantity_in_cart < product.stock_quantity) {
                    return prevCart.map(item =>
                        item.id === product.id
                            ? { ...item, quantity_in_cart: item.quantity_in_cart + 1 }
                            : item
                    );
                } else {
                    setError(`No hay suficiente stock para ${product.name}. Stock actual: ${product.stock_quantity}`);
                    return prevCart;
                }
            } else {
                if (product.stock_quantity > 0) {
                    return [...prevCart, { ...product, quantity_in_cart: 1 }];
                } else {
                    setError(`El producto ${product.name} no tiene stock.`);
                    return prevCart;
                }
            }
        });
        setSuccess(null);
    };

    const handleRemoveProductFromCart = (productId: number) => {
        setCart(prevCart => prevCart.filter(item => item.id !== productId));
    };

    const handleFinalizeSale = async () => {
        if (cart.length === 0) {
            setError("El carrito está vacío.");
            return;
        }

        // user_id debe venir del token JWT o del contexto de autenticación
        // Por ahora, lo hardcodeamos a 1 para pruebas.
        const userId = 1; 

        const salePayload: SalePayload = {
            user_id: userId, 
            items: cart.map(item => ({
                product_id: item.id,
                quantity_sold: item.quantity_in_cart,
                price_at_time_of_sale: item.selling_price,
            })),
            total_amount: cart.reduce((acc, item) => acc + item.selling_price * item.quantity_in_cart, 0),
        };

        try {
            await postSale(salePayload);
            setSuccess("¡Venta registrada con éxito!");
            setCart([]); // Limpiar carrito
            setError(null);
            setSearchResults([]); // Limpiar resultados de búsqueda
            setQuery('');
        } catch (err: any) {
            setError(`Error al registrar la venta: ${err.message}.`);
            setSuccess(null);
        }
    };

    const totalAmount = cart.reduce((acc, item) => acc + item.selling_price * item.quantity_in_cart, 0);

    return (
        <div style={{ padding: '20px' }}>
            <h1>Punto de Venta</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}

            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Buscar producto por nombre o SKU..."
                    style={{ padding: '8px', width: '300px', marginRight: '10px' }}
                />
                <button onClick={handleSearch} style={{ padding: '8px 15px' }}>Buscar</button>
            </div>

            <div style={{ marginBottom: '20px', border: '1px solid #eee', padding: '10px' }}>
                <h3>Resultados de Búsqueda</h3>
                {searchResults.length === 0 ? (
                    <p>No hay resultados.</p>
                ) : (
                    <ul style={{ listStyle: 'none', padding: 0 }}>
                        {searchResults.map(product => (
                            <li key={product.id} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '5px 0', borderBottom: '1px dotted #eee' }}>
                                <span>{product.name} (SKU: {product.sku}) - ${product.selling_price.toFixed(2)} - Stock: {product.stock_quantity}</span>
                                <button onClick={() => handleAddProductToCart(product)} disabled={product.stock_quantity === 0}>Añadir al Carrito</button>
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            <div style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
                <h2>Carrito de Compras</h2>
                {cart.length === 0 ? (
                    <p>El carrito está vacío.</p>
                ) : (
                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                        <thead>
                            <tr>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Producto</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Cantidad</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Precio Unit.</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Subtotal</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Acción</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cart.map(item => (
                                <tr key={item.id}>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{item.name}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{item.quantity_in_cart}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>${item.selling_price.toFixed(2)}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>${(item.selling_price * item.quantity_in_cart).toFixed(2)}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                                        <button onClick={() => handleRemoveProductFromCart(item.id)}>Eliminar</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
                <h3 style={{ textAlign: 'right', marginTop: '10px' }}>Total: ${totalAmount.toFixed(2)}</h3>
            </div>

            <button 
                onClick={handleFinalizeSale} 
                style={{ marginTop: '20px', padding: '10px 20px', fontSize: '1.2em', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
                disabled={cart.length === 0}
            >
                Finalizar Venta
            </button>
        </div>
    );
};

export default POSPage;
