import React, { useState, useEffect } from 'react';
import { APIErrorHandler } from '../services/apiErrorHandler';

interface ErrorNotificationProps {
  error: Error | null;
  onClose: () => void;
  autoClose?: boolean;
  autoCloseDelay?: number;
}

/**
 * Componente para mostrar notificaciones de error
 */
const ErrorNotification: React.FC<ErrorNotificationProps> = ({
  error,
  onClose,
  autoClose = true,
  autoCloseDelay = 5000
}) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    if (error) {
      setIsVisible(true);

      if (autoClose) {
        const timer = setTimeout(() => {
          handleClose();
        }, autoCloseDelay);

        return () => clearTimeout(timer);
      }
    } else {
      setIsVisible(false);
    }

    return undefined;
  }, [error, autoClose, autoCloseDelay]);

  const handleClose = () => {
    setIsVisible(false);
    setTimeout(onClose, 300); // Delay para animación
  };

  const handleRetry = () => {
    // Aquí se podría implementar lógica de retry específica
    handleClose();
  };

  if (!error || !isVisible) {
    return null;
  }

  const errorType = (error as any).type || 'unknown_error';
  const isRecoverable = APIErrorHandler.isRecoverableError(error);
  const userMessage = APIErrorHandler.getUserFriendlyMessage(error);

  const getErrorStyle = () => {
    const baseStyle = {
      position: 'fixed' as const,
      top: '20px',
      right: '20px',
      maxWidth: '400px',
      padding: '16px',
      borderRadius: '8px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
      zIndex: 1000,
      animation: isVisible ? 'slideIn 0.3s ease-out' : 'slideOut 0.3s ease-in',
      color: 'white',
      fontSize: '14px',
      lineHeight: '1.4'
    };

    // Colores según el tipo de error
    switch (errorType) {
      case 'validation_error':
        return { ...baseStyle, backgroundColor: '#f39c12' }; // Naranja
      case 'authentication_error':
      case 'authorization_error':
        return { ...baseStyle, backgroundColor: '#e74c3c' }; // Rojo
      case 'network_error':
      case 'timeout_error':
        return { ...baseStyle, backgroundColor: '#9b59b6' }; // Púrpura
      case 'insufficient_stock_error':
        return { ...baseStyle, backgroundColor: '#e67e22' }; // Naranja oscuro
      default:
        return { ...baseStyle, backgroundColor: '#95a5a6' }; // Gris
    }
  };

  return (
    <>
      <style>
        {`
          @keyframes slideIn {
            from {
              transform: translateX(100%);
              opacity: 0;
            }
            to {
              transform: translateX(0);
              opacity: 1;
            }
          }

          @keyframes slideOut {
            from {
              transform: translateX(0);
              opacity: 1;
            }
            to {
              transform: translateX(100%);
              opacity: 0;
            }
          }
        `}
      </style>

      <div style={getErrorStyle()}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
          <div style={{ flex: 1, marginRight: '12px' }}>
            <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>
              {getErrorTitle(errorType)}
            </div>
            <div style={{ whiteSpace: 'pre-line' }}>
              {userMessage}
            </div>
          </div>

          <button
            onClick={handleClose}
            style={{
              background: 'none',
              border: 'none',
              color: 'white',
              fontSize: '18px',
              cursor: 'pointer',
              padding: '0',
              width: '24px',
              height: '24px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
            title="Cerrar"
          >
            ×
          </button>
        </div>

        {/* Botones de acción */}
        <div style={{ marginTop: '12px', display: 'flex', gap: '8px' }}>
          {isRecoverable && (
            <button
              onClick={handleRetry}
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.2)',
                border: '1px solid rgba(255, 255, 255, 0.3)',
                color: 'white',
                padding: '6px 12px',
                borderRadius: '4px',
                fontSize: '12px',
                cursor: 'pointer'
              }}
            >
              Reintentar
            </button>
          )}

          <button
            onClick={handleClose}
            style={{
              backgroundColor: 'rgba(255, 255, 255, 0.2)',
              border: '1px solid rgba(255, 255, 255, 0.3)',
              color: 'white',
              padding: '6px 12px',
              borderRadius: '4px',
              fontSize: '12px',
              cursor: 'pointer'
            }}
          >
            Cerrar
          </button>
        </div>

        {/* Barra de progreso para auto-close */}
        {autoClose && (
          <div
            style={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              height: '3px',
              backgroundColor: 'rgba(255, 255, 255, 0.3)',
              animation: `shrink ${autoCloseDelay}ms linear`
            }}
          />
        )}
      </div>

      <style>
        {`
          @keyframes shrink {
            from { width: 100%; }
            to { width: 0%; }
          }
        `}
      </style>
    </>
  );
};

/**
 * Obtiene el título apropiado según el tipo de error
 */
const getErrorTitle = (errorType: string): string => {
  switch (errorType) {
    case 'validation_error':
      return 'Error de Validación';
    case 'not_found_error':
      return 'No Encontrado';
    case 'duplicate_error':
      return 'Recurso Duplicado';
    case 'insufficient_stock_error':
      return 'Stock Insuficiente';
    case 'authentication_error':
      return 'Error de Autenticación';
    case 'authorization_error':
      return 'Sin Permisos';
    case 'network_error':
      return 'Error de Conexión';
    case 'timeout_error':
      return 'Tiempo Agotado';
    case 'business_logic_error':
      return 'Error en la Operación';
    case 'database_error':
      return 'Error de Base de Datos';
    case 'internal_server_error':
      return 'Error del Servidor';
    default:
      return 'Error';
  }
};

export default ErrorNotification;
