import React from 'react';
import { PaginationProps } from '../types';

const Pagination: React.FC<PaginationProps> = ({ 
    currentPage, 
    totalPages, 
    onPageChange, 
    disabled = false 
}) => {
    const getPageNumbers = () => {
        const pages = [];
        const maxVisiblePages = 5;

        let startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
        const endPage = Math.min(totalPages, startPage + maxVisiblePages - 1);

        // Ajustar si estamos cerca del final
        if (endPage - startPage + 1 < maxVisiblePages) {
            startPage = Math.max(1, endPage - maxVisiblePages + 1);
        }

        for (let i = startPage; i <= endPage; i++) {
            pages.push(i);
        }

        return pages;
    };

    const buttonStyle = {
        padding: '8px 12px',
        margin: '0 2px',
        border: '1px solid #ddd',
        borderRadius: '4px',
        cursor: 'pointer',
        backgroundColor: '#fff',
        transition: 'all 0.2s'
    };

    const activeButtonStyle = {
        ...buttonStyle,
        backgroundColor: '#007bff',
        color: 'white',
        borderColor: '#007bff'
    };

    const disabledButtonStyle = {
        ...buttonStyle,
        cursor: 'not-allowed',
        opacity: 0.5,
        backgroundColor: '#f8f9fa'
    };

    if (totalPages <= 1) {
        return null; // No mostrar paginación si hay solo una página o menos
    }

    return (
        <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            marginTop: '20px',
            gap: '5px'
        }}>
            {/* Botón Primera página */}
            <button
                style={currentPage === 1 || disabled ? disabledButtonStyle : buttonStyle}
                onClick={() => onPageChange(1)}
                disabled={currentPage === 1 || disabled}
            >
                ««
            </button>

            {/* Botón Página anterior */}
            <button
                style={currentPage === 1 || disabled ? disabledButtonStyle : buttonStyle}
                onClick={() => onPageChange(currentPage - 1)}
                disabled={currentPage === 1 || disabled}
            >
                ‹
            </button>

            {/* Números de página */}
            {getPageNumbers().map(pageNum => (
                <button
                    key={pageNum}
                    style={pageNum === currentPage ? activeButtonStyle : (disabled ? disabledButtonStyle : buttonStyle)}
                    onClick={() => onPageChange(pageNum)}
                    disabled={disabled}
                >
                    {pageNum}
                </button>
            ))}

            {/* Botón Página siguiente */}
            <button
                style={currentPage === totalPages || disabled ? disabledButtonStyle : buttonStyle}
                onClick={() => onPageChange(currentPage + 1)}
                disabled={currentPage === totalPages || disabled}
            >
                ›
            </button>

            {/* Botón Última página */}
            <button
                style={currentPage === totalPages || disabled ? disabledButtonStyle : buttonStyle}
                onClick={() => onPageChange(totalPages)}
                disabled={currentPage === totalPages || disabled}
            >
                »»
            </button>

            {/* Información de página */}
            <span style={{ 
                marginLeft: '15px', 
                fontSize: '14px', 
                color: '#666' 
            }}>
                Página {currentPage} de {totalPages}
            </span>
        </div>
    );
};

export default Pagination;
